Date.prototype.clearTime = function() {
    this.setHours(0);
    this.setMinutes(0);
    this.setSeconds(0);
    this.setMilliseconds(0);
    return this;
};

Date.prototype.getTodayNoon = function() {
    var day = 86400 * 1000;
    var hour = 60 * 60 * 1000;
    var thisday = new Date(this);
    thisday.clearTime();
    thisday -= 0;
    thisday += 12 * hour;
    return new Date(thisday);
};

Date.prototype.getTodayEvening = function() {
    var day = 86400 * 1000;
    var hour = 60 * 60 * 1000;
    var thisday = new Date(this);
    thisday.clearTime();
    thisday -= 0;
    thisday += 18 * hour;
    return new Date(thisday);
};

Date.prototype.getToday = function() {
    var day = 86400 * 1000;
    var thisday = new Date(this);
    thisday.clearTime();
    return new Date(thisday);
};

Date.prototype.getYesterday = function() {
    var day = 86400 * 1000;
    var thisday = new Date(this);
    thisday.clearTime();
    thisday -= day;
    return new Date(thisday);
};

Date.prototype.getLastWeek = function() {
    var day = 86400 * 1000;
    var thisday = new Date(this);
    // thisday.clearTime();
    thisday -= 7 * day;
    return new Date(thisday);
};

Date.prototype.getFirstDay = function() {
    var day = 86400 * 1000;
    var thisday = new Date(this);
    thisday.clearTime();
    thisday -= thisday.getDay() * day;
    return new Date(thisday);
};

